mapper.select:

select 

from t_operation_push_erp
where type = #{type,jdbcType=INTEGER}
  and audit_date = #{auditDate,jdbcType=VARCHAR}
  and pay_parent_channel = #{payParentChannel,jdbcType=TINYINT}
  and vendor_code = #{vendorCode,jdbcType=VARCHAR}

mapper.delete:

delete from t_operation_push_erp
where type = #{type,jdbcType=INTEGER}
  and audit_date = #{auditDate,jdbcType=VARCHAR}
  and pay_parent_channel = #{payParentChannel,jdbcType=TINYINT}
  and vendor_code = #{vendorCode,jdbcType=VARCHAR}
